mapper.update:
UPDATE
  		TB_OPERATING_HOURS
  SET(
		START_TIME = #{startTime},
		END_TIME = #{endTime}
  )
 WHERE
        RESTAURANT_NO = #{restaurantNo}
  AND
        WEEKDAY = #{weekDay}

UPDATE
  		TB_BREAKTIME
  SET(
		BREAK_START_TIME = #{breakStartTime},
		BREAK_END_TIME = #{breakEndTime}
  )
 WHERE
		OPERATING_HOURS_NO = #{operatingHoursNo}
